sql-case.value:
CREATE OR REPLACE FUNCTION increment(i integer) RETURNS integer AS
        $$
            BEGIN
                RETURN i + 1;
            END;
        $$ LANGUAGE plpgsql

CREATE PROCEDURE insert_data(a integer, b integer)
        LANGUAGE SQL
        AS $$
        INSERT INTO tbl VALUES (a);
        INSERT INTO tbl VALUES (b);
        $$
