command.create-order-item-table-rule:
CREATE SHARDING TABLE RULE t_order_item(
RESOURCES(ds_0,ds_1),
SHARDING_COLUMN=order_id,
TYPE(NAME=hash_mod,PROPERTIES("sharding-count"=4)),
KEY_GENERATE_STRATEGY(COLUMN=order_id,TYPE(NAME=snowflake))
);

command.source-add-resource-template:
ADD RESOURCE ds_0 (
URL="${ds0}",
USER=${user},
PASSWORD=${password}
), ds_1 (
URL="${ds1}",
USER=${user},
PASSWORD=${password}
)

command.target-add-resource-template:
ADD RESOURCE ds_2 (
URL="${ds2}",
USER=${user},
PASSWORD=${password}
),ds_3 (
URL="${ds3}",
USER=${user},
PASSWORD=${password}
),ds_4 (
URL="${ds4}",
USER=${user},
PASSWORD=${password}
)
